def find_marker(buffer, len = 4)
  last4 = []
  count = 0
  buffer.chars.each do |char|
    last4.length < len ? last4 << char : last4 = last4[1..-1].push(char)
    count += 1
    break if last4.uniq.length == len
  end
  count
end

